import React, { memo, useState } from 'react';
import Background from '../components/Background';
import Logo from '../components/Logo';
import Header from '../components/Header';
import Paragraph from '../components/Paragraph';
import Button from '../components/Button';
import * as ImagePicker from 'expo-image-picker';
import { Image, Platform } from 'react-native';
import global from '../global.js';

const Dashboard = ({ navigation, route }) => {

  const [photo, setPhoto] = useState(null);

  const [profBio, setprofBio] = useState(global.Profile.bio);

  const _logout = () => {
    fetch('http://159.89.153.162:5000/api/v1/auth/logout', {
      method: 'GET'
    });
    global.Token = '';
    global.User = '';
    global.Profile = '';
    navigation.navigate('Home');
  }

  const _handleChoosePhoto = async () => {
    try {
      let result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.All,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 1,
      });
      if (!result.cancelled) {
        setPhoto(result);
      }
      console.log(result);
    } catch (error) {
      console.log(error);
    }
  };

  const _getPhoto = () => {
    fetch('http://159.89.153.162:5000/api/v1/profile/0001/photo', {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + global.Token
      }
    });
  }

  const _uploadPhoto = () => {
    var data = new FormData();
    data.append('file', {
      uri: photo.uri,
      type: 'image/jpeg',
      name: 'photo.jpg'
    });
    fetch('http://159.89.153.162:5000/api/v1/profile/0001/photo', {
      method: 'PUT',
      headers: {
        'Content-Type': 'multipart/form-data;',
        'Authorization': 'Bearer ' + global.Token
      },
      body: data
    });
  }

  const _navUpdateUserInfo = () => {
    navigation.navigate('UpdateUserInformation');
  }

  const _navCreateUpdateProfile = () => {
    navigation.navigate('CreateUpdateProfile', { action: 'Update' });
  }

  const _changeName = () => {
    console.log(global.Profile.bio);
    setprofBio(global.Profile.bio);
  }

  componentDidMount = () => {
    console.log(global.Profile.bio);
    setprofBio(global.Profile.bio);
  }

  return (
    <Background>
      {photo && (
        <Image
          source={{ uri: photo.uri }}
          style={{ width: 300, height: 300 }}
        />
      )}
      <Header>{global.User.firstName} {global.User.lastName}</Header>
      <Paragraph>
        Home Phone: {global.Profile.homePhone}
      </Paragraph>
      <Paragraph>
        Work Phone: {global.Profile.workPhone}
      </Paragraph>
      <Paragraph>
        Cell Phone: {global.Profile.cellPhone}
      </Paragraph>
      <Paragraph>
        Address: {global.Profile.address}
      </Paragraph>
      <Paragraph>
        Bio: {profBio}
      </Paragraph>
      <Paragraph>
        Hobbies: {global.Profile.hobbies.join(', ')}
      </Paragraph>
      <Button mode="outlined" onPress={_changeName}>
        Change Name
      </Button>
      <Button mode="outlined" onPress={_logout}>
        Logout
      </Button>
      <Button mode="outlined" onPress={_navUpdateUserInfo}>
        Update User
      </Button>
      <Button mode="outlined" onPress={_navCreateUpdateProfile}>
        Update Profile
      </Button>
      <Button mode="outlined" onPress={_handleChoosePhoto}>
        Choose Photo
      </Button>
      <Button mode="outlined" onPress={_uploadPhoto}>
        Send Photo
      </Button>
      <Button mode="outlined" onPress={_getPhoto}>
        Get Photo
      </Button>
    </Background>
  );
}

export default memo(Dashboard);
